import React, { useState } from 'react';

const BookingForm = () => {
    const [date, setDate] = useState(""); // Fixed typo in the variable name



    const handleChange = (e) => {
        setDate(e.target.value); // Set the value of the date state
        props.dispatch(e);  // Dispatch the event to update Redux
    };

    return (
        <header>
            <section>
                <form>
                    <fieldset>
                        <div>
                            <label htmlFor='book-date'>Choose Date</label>
                            <input id='book-date' value={date} onChange={handleChange} type='date' required/> {/* Pass the handleChange function directly */}
                        </div>
                    </fieldset>
                </form>
            </section>
        </header>
    );
};

export default BookingForm;
