import React, { useState } from 'react';

const BookingForm = (props) => {
    const [date, setDate] = useState("");
    const [time, setTime] = useState("");
    const [guests, setGuests] = useState("");
    const [occasion, setOccasion] = useState(""); 

    const handleSubmit = (e) => {
        e.preventDefault();
        // Do something with the form data or dispatch an action
    }

    const handleTimeChange = (e) => {
        setTime(e.target.value); // Set the value of the time state
    };

    return (
        <header>
            <section>
                <form onSubmit={handleSubmit}>
                    <fieldset>
                        <div>
                            <label htmlFor='book-date'>Choose Date</label>
                            <input id='book-date' value={date} onChange={(e) => setDate(e.target.value)} type='date' required/>
                        </div>
                        {/* Time field */}
                        <div>
                            <label htmlFor='book-time'>Choose a Time</label>
                            <select id="book-time" value={time} onChange={handleTimeChange} required>
                                <option value="">Select a Time</option>
                                {props.availableTimes.map((availableTime, index) => (
                                    <option key={index}>{availableTime}</option>
                                ))}
                            </select>
                        </div>
                    </fieldset>
                </form>
            </section>
        </header>
    );
};

export default BookingForm;
